import { useParams } from "react-router-dom"
import axios from 'axios'
import { useState, useEffect } from "react";
function WordDefinitions(){
    const { word } = useParams();
    const [definition, setDefinition]= useState()
    console.log(word)
    useEffect(()=>{
    axios.post("http://localhost:8080/words/definition",{word: word}).then((response)=>{
        setDefinition(response.data)
        console.log(response)
    }).catch((error)=>{console.log(error)})
},[word])
    console.log('hi')
    return(<>
    <h1>{word}</h1>
    <h3>{`Part of Speech: ${definition?.partOfSpeech}`}</h3>
    {definition?.definitions?.map((element, index)=>{
        return(<div key={index}>
        <p>{`definition: ${element?.definition}`}</p>
        <p>{`synonym: ${element?.synonyms[0]}`}</p>
        <p>{`antonym: ${element?.antonyms[0]}`}</p>
        <p>{`example: ${element?.example}`}</p>
        </div>)
    })}
    
    </>)
}
export default WordDefinitions;
